import { types } from "../store/types";
import { put, takeLatest, call } from "redux-saga/effects";

import axios from "axios";

axios.defaults.baseURL =
  "https://8dd5cd27-a5cb-4244-b2b5-0ed958f59494.mock.pstmn.io/api/v1/investments";
axios.defaults.headers.post["Content-Type"] = `application/json`;

function* fetchAllMutualFunds(func) {
  try {
    yield put({ type: types.FETCHING_DATA });
    let response = yield call(() => fetchData("/mutual-funds"));
    yield put({ type: types.GET_ALL_ETF, payload: response.mutual_funds });
    console.log(response.mutual_funds, response);
  } catch (error) {}
}

async function fetchData(endpoint) {
  let res = await axios.get(endpoint);
  console.log(res)
  return res.data ;
}

function* fetchAllEtfs(func) {
  yield put({ type: types.FETCHING_DATA });
  let response = yield call(() => fetchData("/etf"));
  yield put({ type: types.GET_ALL_ETF, payload: response.etf });
  console.log(response.etf, response);
}

export function* watchRequirements() {
  console.log("watcher");
  yield takeLatest(types.FETCH_ALL_MUTUAL_FUNDS, fetchAllMutualFunds);
  yield takeLatest(types.FETCH_ALL_ETF, fetchAllEtfs);
}
