import React, { useEffect, useState } from "react";
import { data } from "../data";
import { types } from "../store/types";
import Card from "./Card";

function Cards({onSelectingMutualFund, onSelectingETF}) {
  let [fundType, setFundType] = useState("mf");
  useEffect(() => {

    switch(fundType) {
        case "mf": onSelectingMutualFund();
                    break;
        case "etf": onSelectingETF();
                    break;
        default: return
    }

  }, [fundType])
  return (
    <div>
      <select onChange={() => setFundType(this.value)}>
        <option value="mf">Mutual Funds</option>
        <option value="etf">ETFs</option>
      </select>
      {data?.mf.map((fund) => {
        return <Card fund={fund} />;
      })}
    </div>
  );
}

function mapDispatchToProp(dispatch) {
    return {
        onSelectingMutualFund: () => dispatch({ type: types.FETCH_ALL_MUTUAL_FUNDS }),
        onSelectingETF: () => dispatch({ type: types.FETCH_ALL_MUT }),
    }
}

export default connect(mapStateToProp, mapDispatchToProp)(Cards);
