import { types } from "../store/types";
// import {delay} from "redux-saga";
import { put, takeLatest } from "redux-saga/effects";

import axios from "axios";

axios.defaults.baseURL =
  "https://8dd5cd27-a5cb-4244-b2b5-0ed958f59494.mock.pstmn.io/api/v1/investments";
axios.defaults.headers.post["Content-Type"] = `application/json`;

async function fetchAllMutualFunds(func) {
  try {
    alert("Hello")
    let mutualFunds = await axios.get("/mutual-funds");
    console.log(mutualFunds);
  } catch (error) {
    console.log(error);
  }
}

async function fetchAllEtfs(func) {
  try {
    let etf = await axios.get("/etf");
    console.log(etf);
  } catch (error) {
    console.log(error);
  }
}

export function* watchRequirements() {
  yield takeLatest(types.FETCH_ALL_MUTUAL_FUNDS, fetchAllMutualFunds);
  yield takeLatest(types.FETCH_ALL_ETF, fetchAllEtfs);
}
